function prepareResponse(allItems) {
    let emails = [];

    // sort | desc 
    allItems.sort((a,b) => (a._id < b._id) ? 1: -1);

    // remove duplicates
    let response = allItems.filter((item, key) => {
        if(emails.indexOf(item.email) == -1) {
            emails.push(item.email);
            return item;
        }
    });

    runtime.done({output: response});
}

