function createFilter(allItems) {
    // output
    let filters = [];

    // loop through items
    allItems.map((item, key) => {

        // get created date 
        let createdAtDate = new Date(item.createdat);
    
        // get rounded date
        let roundedDate = roundOffTime(createdAtDate); 
        // create min max dates 
        let minDate = new Date(roundedDate.toISOString());
        let maxDate = new Date(roundedDate.toISOString());

        minDate = new Date(minDate.setMinutes(minDate.getMinutes() - 5));
        maxDate = new Date(maxDate.setMinutes(maxDate.getMinutes() + 5));

        let filter = {
            "createdat": {
                "$gte": minDate.toISOString(),
                "$lte": maxDate.toISOString()
            }
            // may need to check the location
        }

        filters.push(JSON.stringify(filter));
    });

    runtime.done({filters: filters, email: inputs.email});

}

// round off time to nearest 5 mins
function roundOffTime(date) {
    var time = 1000 * 60 * 5;
    var rounded = new Date(Math.round(date.getTime() / time) * time);
    return rounded;
}
